import './About.css'
import Timeline from './Timeline.js'
import Chevron from './chevron.svg'
import { useEffect, useState } from 'react';

const About = () => {
    const [show, setShow] = useState(true)
    const hideContentOnScroll = () => {
        if(window.scrollY > 100)
        {
            setShow(false)
            console.log(123)
        }
        else
        {
            setShow(true)
        }
    }

    useEffect(() => {
        window.addEventListener('scroll', hideContentOnScroll)
        return () => {
            window.removeEventListener('scroll', hideContentOnScroll)
        }
    }, [])

    return (
        <div className='about-container'>
            <div className='welcome-container'>
                <div className={`header-container' ${show}`}>
                    <h1 className='header'>hi, i'm <span class='header-inner-name' >Josh</span> ðŸ‘‹</h1>
                    <h2 class='subheader'>a software development enthusiast</h2>
                </div>

                <div class='scroller-container'>
                    <h1 class='scroller-header'><span class='header-inner-name'>scroll</span> to learn more about me</h1>
                    <div class='chevron-container'>
                        <img class='chevron' src={Chevron}></img>
                        <img class='chevron-delay-1' src={Chevron}></img>
                        <img class='chevron-delay-2' src={Chevron}></img>
                    </div>
                </div>

            </div>
            <Timeline />
        </div>

    );
};

export default About;
